sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var request = new sn_ws.RESTMessageV2('Incident Integration', 'Incident_Record_Creation');
	request.setStringParameterNoEscape('caller', current.caller_id);
	request.setStringParameterNoEscape('short_description', current.short_description);
	request.setStringParameterNoEscape('category', current.category);
	request.setStringParameterNoEscape('impact', current.impact);
	request.execute();

	var response=request.execute();
	var requestBody=request.getRequestBody();
	var responseBody=response.getBody();
	var httpStatus=response.getStatusCode();

	responseJSON=responseBody.substring(10,responseBody.length-1);
	parsedJSON=JSON.parse(responseJSON);

	var targetIncidentNumber=parsedJSON['number'];
	var targetIncidentsysid=parsedJSON['sys_id'];

	var logString='Target Incident Number - '+targetIncidentNumber+ '\n Target Incident Sys_id' +targetIncidentsysid;
	gs.log(logString, 'BR- Create Incident Record n Target Instance')

})(current, previous);
